import {
	all,
	fork,
	takeLatest,
	takeEvery,
	call,
	put,
	take
} from "redux-saga/effects";
import { LOG_IN, LOG_IN_FAILURE, LOG_IN_SUCCESS } from "../reducers/user";

const HELLO_SAGA = "HELLO_SAGA";

function loginAPI() {}

function* login() {
	try {
		yield call(loginAPI);
		yield put({
			type: LOG_IN_SUCCESS
		});
	} catch (e) {
		console.error(e);
		yield put({
			type: LOG_IN_FAILURE
		});
	}
}
function* watchLogin() {
	while (true) {
		yield take(LOG_IN);
		yield put({
			type: LOG_IN_SUCCESS
		});
	}
}

export default function* userSaga() {
	yield all([watchLogin()]);
}
