import {
	all,
	fork,
	takeLatest,
	takeEvery,
	call,
	put,
	take
} from "redux-saga/effects";
import { LOG_IN, LOG_IN_FAILURE, LOG_IN_SUCCESS } from "../reducers/user";

const HELLO_SAGA = "HELLO_SAGA";

function* login() {
	try {
		yield delay(100);
		yield put({
			type: LOG_IN_SUCCESS
		});
	} catch (e) {
		console.error(e);
		yield put({
			type: LOG_IN_FAILURE
		});
	}
}
function* watchLogin() {
	yield takeEvery(LOG_IN, login);
}

export default function* userSaga() {
	yield all([watchLogin()]);
}
